        public static ArrayList<Book> getListData() {
            ArrayList<Book> list = new ArrayList<>();
            for (String[] bookData : booksdata) {
                Book book = new Book();
                book.setName(bookData[0]);
                book.setTitle(bookData[1]);
                book.setDetail(bookData[2]);
                book.setPhoto(bookData[3]);

                list.add(book);
            }
            return list;
        }
